sp_widget.client_script:
function($location, $scope, spUtil) {
  /* widget controller */
	var c = this;

	//Redirect to Contact form w/ Contact Form sys_id and section label
	c.formDetail = function(formid, section){
		$location.url("onboard/sp?id=formdetail&formid=" + formid + '&section=' + section);
	};

	//Compares the current date with the Contact Form Due date
	c.dates = function(){
			var today = new Date();
			var dd = today.getDate();
			var mm = today.getMonth()+1; //January is 0!
			var yyyy = today.getFullYear();
			if(dd<10) {
				dd = '0'+dd
			}
			if(mm<10) {
				mm = '0'+mm
			} 
			today = yyyy + '-' + mm + '-' + dd;

			var dateOut1 = new Date($scope.data.ticketInfo.dueDate);
			var dateOut2 = new Date(today);

			var timeDiff = Math.abs(dateOut2.getTime() - dateOut1.getTime());
			var diffDays = Math.ceil(timeDiff / (1000 * 3600 * 24)); 

			if(dateOut1 < dateOut2){
				diffDays = diffDays + ' Days Passed Due!';
			}else{
				diffDays = diffDays + ' Days'
			}
		$scope.data.ticketInfo.dueDate = diffDays;
	}

	c.dates();

	//Dynamic Progress Bar width based on the number of finished flags
	$scope.progress = function(information){
		document.getElementById("progressbar").style.width = information.percent + '%';
	}

	spUtil.recordWatch($scope, "x_143728_onboard_contact_information", "", function(name, data){
		spUtil.update($scope);
	});

	spUtil.recordWatch($scope, "x_143728_onboard_process", "", function(name, data){
		spUtil.update($scope);
	});

}

