sp_widget.script:
(function() {
	var gr = new GlideRecord('x_143728_onboard_contact_information');
	gr.addQuery('user',gs.getUserID());
	gr.query();
	if(gr.next()){
		var processTicket = new GlideRecord('x_143728_onboard_process');
		processTicket.addQuery('sys_id',gr.process_ticket);
		processTicket.query();
		if(processTicket.next()){
			//Returns a 'percent' of finished sections for the Contact Form
			var done = 0;
			var arr = [];
			arr.push(processTicket.getDisplayValue('account_setup_flag').toString());
			arr.push(processTicket.getDisplayValue('bank_info_flag').toString());
			arr.push(processTicket.getDisplayValue('benefits_flag').toString());
			arr.push(processTicket.getDisplayValue('contact_information_flag').toString());
			arr.push(processTicket.getDisplayValue('office_setup_flag').toString());
			arr.push(processTicket.getDisplayValue('personal_information_flag').toString());
			for(i = 0; i < arr.length; i++){
				if(arr[i] == "true"){
					done++;
				}
			}

			var percent = done/arr.length;

			data.information = {
				user:  gr.getDisplayValue('user.name'),
				sys_id: gr.getDisplayValue('sys_id'),
				percent: (percent*100).toString().substr(0, 4)
			};

			data.ticketInfo = {};
			data.ticketInfo.accountFlag = processTicket.getDisplayValue('account_setup_flag').toString();
			data.ticketInfo.bankFlag = processTicket.getDisplayValue('bank_info_flag').toString();
			data.ticketInfo.benefitFlag = processTicket.getDisplayValue('benefits_flag').toString();
			data.ticketInfo.contactFlag = processTicket.getDisplayValue('contact_information_flag').toString();
			data.ticketInfo.officeFlag = processTicket.getDisplayValue('office_setup_flag').toString();
			data.ticketInfo.personalFlag = processTicket.getDisplayValue('personal_information_flag').toString();
			data.ticketInfo.start_date = processTicket.getDisplayValue('start_date');
			data.ticketInfo.dueDate = processTicket.getDisplayValue('due_date');
		}
	}


})();
