sp_widget.script:
(function() {
	data.sys_id = $sp.getParameter('formid');		
	data.section = $sp.getParameter('section');

	if(!data.process) 
		data.process = {};

	var infoRecord = new GlideRecord('x_143728_onboard_contact_information');

	infoRecord.get(data.sys_id);


	// Pull User id using reference field
	var guideSysId = "";
	if (input && input.setToggl)
		togglSysId = input.setToggl;
	else
		togglSysId = gs.getUser();

	// Create user record with User id
	var togglGR = new GlideRecord('sys_user');
	togglGR.get('sys_id', togglSysId);

	data.toggl_buddy = {};
	$sp.getRecordDisplayValues(data.toggl_buddy, togglGR, 'sys_id,user_name,first_name,last_name,email');

	//Saves the information, does not trigger the flag
	if(input && input.action == "saveContact") {
		infoRecord.access_points = this.input.information.access_points;
		infoRecord.google_drive = this.input.information.google_drive;
		infoRecord.wiki = this.input.information.wiki;
		infoRecord.email_group = this.input.information.email_group;
		infoRecord.toggl_account = this.input.information.toggl_account;
		infoRecord.toggl_buddy = this.input.toggl_buddy.sys_id;
		infoRecord.gmail_signature = this.input.information.gmail_signature;
		infoRecord.guidelines = this.input.information.guidelines;
		infoRecord.culture_event = this.input.information.culture_event;
		infoRecord.update();
		gs.addInfoMessage('Account Setup Saved');
	}	

	//Validates inputs, on successful submittion triggers the flag
	if(input && input.action == "submitContact") {
		if( this.input.information.access_points == 'false' || this.input.information.google_drive == 'false' ||
			 this.input.information.wiki == 'false' || this.input.information.email_group == 'false' ||
			 this.input.information.toggl_account == 'false' || this.input.toggl_buddy.sys_id == '' ||
			 this.input.information.gmail_signature == 'false' || this.input.information.guidelines == 'false' ||
			 this.input.information.culture_event == ''){
			gs.addErrorMessage('Please fill out all fields before submission.')
		}else{
			infoRecord.access_points = this.input.information.access_points;
			infoRecord.google_drive = this.input.information.google_drive;
			infoRecord.wiki = this.input.information.wiki;
			infoRecord.email_group = this.input.information.email_group;
			infoRecord.toggl_account = this.input.information.toggl_account;
			infoRecord.toggl_buddy = this.input.toggl_buddy.sys_id;
			infoRecord.gmail_signature = this.input.information.gmail_signature;
			infoRecord.guidelines = this.input.information.guidelines;
			infoRecord.culture_event = this.input.information.culture_event;
			infoRecord.update();
			var processUpdate = new GlideRecord('x_143728_onboard_process');
			processUpdate.addQuery('sys_id', infoRecord.process_ticket);
			processUpdate.query();
			if(processUpdate.next()){
				processUpdate.account_setup_flag = true;
				processUpdate.update();
			}
			gs.addInfoMessage('Account Setup Submitted');
		}
	}	


	data.information = {};
	data.information.sys_id = infoRecord.getDisplayValue('sys_id');
	data.information.user = infoRecord.getDisplayValue('user.name');
	data.information.access_points = infoRecord.getDisplayValue('access_points');
	data.information.google_drive = infoRecord.getDisplayValue('google_drive');
	data.information.wiki = infoRecord.getDisplayValue('wiki');
	data.information.email_group = infoRecord.getDisplayValue('email_group');
	data.information.toggl_account = infoRecord.getDisplayValue('toggl_account');
	data.information.toggl_buddy = infoRecord.getDisplayValue('toggl_buddy.name');
	data.information.toggl_buddy_sysid = infoRecord.getDisplayValue('toggl_buddy.sys_id');
	data.information.gmail_signature = infoRecord.getDisplayValue('gmail_signature');
	data.information.guidelines = infoRecord.getDisplayValue('guidelines');
	data.information.culture_event = infoRecord.getDisplayValue('culture_event');
	data.information.process_ticket = infoRecord.getDisplayValue('process_ticket.number');
	data.information.startDate = infoRecord.getDisplayValue('process_ticket.start_date');
	data.information.dueDate = infoRecord.getDisplayValue('process_ticket.due_date');
	data.information.user = infoRecord.getDisplayValue('user.name');
})();
