sp_widget.client_script:
function($scope, spUtil, $location) {
	/* widget controller */
	var c = this;

	//Sets the date to a format that the front-end can display correctly
	$scope.data.process.start_date = new Date($scope.data.process.start_date);
	$scope.data.process.due_date = new Date($scope.data.process.due_date);

	$scope.updateTicket = function() {
		$scope.data.action = "updateTicket";
		$scope.server.update().then(function() {
			$scope.data.action = undefined;
		});
	}

	spUtil.recordWatch($scope, "x_143728_onboard_process", "", function(name, data){
		spUtil.update($scope);
	});

}

sp_widget.script:
(function() {
	data.sys_id = $sp.getParameter('processid');	

	var processRecord = new GlideRecord('x_143728_onboard_process');
	processRecord.get(data.sys_id);

	if(input && input.action == "updateTicket") {
		processRecord.start_date = this.input.process.start_date.toString();
		processRecord.contact_information_due_date = this.input.process.contactDue.toString();
		processRecord.account_setup_due_date = this.input.process.accountDue.toString();
		processRecord.bank_info_due_date = this.input.process.bankDue.toString();
		processRecord.benefits_due_date = this.input.process.benefitsDue.toString();
		processRecord.office_setup_due_date = this.input.process.officeDue.toString();
		processRecord.personal_information_due_date = this.input.process.personalDue.toString();
		processRecord.update();
		gs.addInfoMessage('Dates have been updated');
	}	

	data.process = {};
	data.process.guide = processRecord.getDisplayValue('orientation_guide.name');
	data.process.employee = processRecord.getDisplayValue('employee.name');
	data.process.executive = processRecord.getDisplayValue('executive.name');
	data.process.start_date = processRecord.getDisplayValue('start_date');
	data.process.due_date = processRecord.getDisplayValue('due_date');


})();
