sp_widget.script:
(function() {
  data.sys_id = $sp.getParameter('formid');		
	data.section = $sp.getParameter('section');

	if(!data.process) 
		data.process = {};

	var infoRecord = new GlideRecord('x_143728_onboard_contact_information');

	infoRecord.get(data.sys_id);

	data.information = {};
	data.information.sys_id = infoRecord.getDisplayValue('sys_id');
	data.information.user = infoRecord.getDisplayValue('user.name');
	data.information.accountFlag = infoRecord.getDisplayValue('process_ticket.account_setup_flag');
	data.information.bankFlag = infoRecord.getDisplayValue('process_ticket.bank_info_flag');
	data.information.benefitFlag = infoRecord.getDisplayValue('process_ticket.benefits_flag');
	data.information.contactFlag = infoRecord.getDisplayValue('process_ticket.contact_information_flag');
	data.information.officeFlag = infoRecord.getDisplayValue('process_ticket.office_setup_flag');
	data.information.personalFlag = infoRecord.getDisplayValue('process_ticket.personal_information_flag');

})();
