sp_widget.client_script:
function(spUtil, $scope, $location) {
  /* widget controller */
  var c = this;

	//Redirect to Contact form w/ Contact Form sys_id and section label
	c.formDetail = function(formid, section){
		$location.url("onboard/sp?id=formdetail&formid=" + formid + '&section=' + section);
	};

	spUtil.recordWatch($scope, "x_143728_onboard_process", "", function(name, data){
		spUtil.update($scope);
	});
}

sp_widget.css:
//Container
.container{
  background-color: white;
  width: 100%;
}

//Global Style
h4{
  color:white;
}

//Div for users with no Contaact Form
#noOnboard{
  text-align: center;
}

//Individual Form section cards
.section{
  text-align: center;
  width: 100%;
  padding: 10px 0px 5px 0px;
  margin: 5px 0px;
  background-color: #484848;
}
.section:hover {
  position: relative;
  z-index: 1000;
  cursor: pointer;
  -webkit-transform: scale(1.05);
  transform: scale(1.05);
  -moz-box-shadow: 0 0 20px #ccc;
	-webkit-box-shadow: 0 0 20px #ccc;
	box-shadow: 0 0 20px black;
}

sp_widget.script:
(function() {

	var gr = new GlideRecord('x_143728_onboard_process');
	gr.addQuery('employee',gs.getUserID());
	gr.query();
	if(gr.next()){
		//Returns the total unfinished Contact Form sections
		var todo = 0;
		var arr = [];
		arr.push(gr.getDisplayValue('account_setup_flag').toString());
		arr.push(gr.getDisplayValue('bank_info_flag').toString());
		arr.push(gr.getDisplayValue('benefits_flag').toString());
		arr.push(gr.getDisplayValue('contact_information_flag').toString());
		arr.push(gr.getDisplayValue('office_setup_flag').toString());
		arr.push(gr.getDisplayValue('personal_information_flag').toString());
		for(i = 0; i < arr.length; i++){
			if(arr[i] == "false"){
				todo++;
			}
		}

		data.ticket = {};
		data.ticket.count = todo;
		data.ticket.accountFlag = gr.getDisplayValue('account_setup_flag').toString();
		data.ticket.bankFlag = gr.getDisplayValue('bank_info_flag').toString();
		data.ticket.benefitFlag = gr.getDisplayValue('benefits_flag').toString();
		data.ticket.contactFlag = gr.getDisplayValue('contact_information_flag').toString();
		data.ticket.officeFlag = gr.getDisplayValue('office_setup_flag').toString();
		data.ticket.personalFlag = gr.getDisplayValue('personal_information_flag').toString();
		data.ticket.start_date = gr.getDisplayValue('start_date');
		data.ticket.dueDate = gr.getDisplayValue('due_date');
	}

	data.contact = {};

	//Grabbing the Contact Information for the $location in the Client Script
	var contact = new GlideRecord('x_143728_onboard_contact_information');
	contact.addQuery('user',gs.getUserID());
	contact.query();
	if(contact.next()){
		data.contact.sys_id = contact.getDisplayValue('sys_id').toString();
	}else{
		//Used to trigger the 'noOnboard' div
		data.contact.sys_id = '';
	}

})();
