sp_widget.script:
(function() {
  data.onboards = [];

	var gr = new GlideRecord('x_143728_onboard_process');
		gr.addQuery('active',false)
		gr.query();
	while(gr.next()){
		var process = {};

		var contactRecord =  new GlideRecord('x_143728_onboard_contact_information');
		contactRecord.addQuery('process_ticket', gr.getDisplayValue('sys_id'));
		contactRecord.query();
		if(contactRecord.next()){
			process.contact_sysid = contactRecord.getDisplayValue('sys_id');
		}

		process.sys_id = gr.getDisplayValue('sys_id');
		process.employee_sysid = gr.getDisplayValue('employee.sys_id');
		process.employee = gr.getDisplayValue('employee.name');
		process.guide = gr.getDisplayValue('orientation_guide.name');
		process.accountFlag = gr.getDisplayValue('account_setup_flag');
		process.bankFlag = gr.getDisplayValue('bank_info_flag');
		process.benefitFlag = gr.getDisplayValue('benefits_flag');
		process.contactFlag = gr.getDisplayValue('contact_information_flag');
		process.officeFlag = gr.getDisplayValue('office_setup_flag');
		process.personalFlag = gr.getDisplayValue('personal_information_flag');
		data.onboards.push(process);
	}
})();
